import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { usePatients } from "@/hooks/use-patients";
import { useCreateAppointment } from "@/hooks/use-appointments";
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@shared/routes";
import { useToast } from "@/hooks/use-toast";
import { StatCard } from "@/components/stat-card";
import { apiRequest, queryClient } from "@/lib/queryClient";

import FullCalendar from '@fullcalendar/react';
import timeGridPlugin from '@fullcalendar/timegrid';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

import { DateRangePicker } from "@/components/ui/date-range-picker";
import { eachDayOfInterval } from "date-fns";
import { DateRange } from "react-day-picker";

export default function AgendaPage() {
  const [viewRange, setViewRange] = useState({ 
    start: format(new Date(), 'yyyy-MM-dd'),
    end: format(new Date(), 'yyyy-MM-dd')
  });
  const [selectedDoctorId, setSelectedDoctorId] = useState<number | undefined>();
  const [editingAppointment, setEditingAppointment] = useState<AppointmentWithDetails | null>(null);
  const [isAptDialogOpen, setIsAptDialogOpen] = useState(false);
  const [isAvailabilityDialogOpen, setIsAvailabilityDialogOpen] = useState(false);
  const [availabilityDateRange, setAvailabilityDateRange] = useState<DateRange | undefined>({
    from: new Date(),
    to: new Date(),
  });

  const { data: allAppointments, isLoading: isLoadingAppointments } = useAppointments({ 
    startDate: viewRange.start, 
    endDate: viewRange.end 
  });

  const getUnavailableTimes = (date: string, doctorId: number) => {
    if (!allAppointments) return [];
    return allAppointments
      .filter(apt => apt.date === date && apt.doctorId === doctorId && apt.status !== 'cancelado')
      .map(apt => apt.startTime);
  };

  const isTimeSlotOccupied = (time: string, date: string, doctorId: number) => {
    const unavailable = getUnavailableTimes(date, doctorId);
    return unavailable.includes(time);
  };
  const { data: patients } = usePatients();
  const { data: doctors } = useQuery<User[]>({ 
    queryKey: [api.users.list.path, { role: 'doctor' }],
    queryFn: async () => {
      const res = await fetch(`${api.users.list.path}?role=doctor`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch doctors");
      return res.json();
    }
  });

  const { data: availabilityExceptions } = useQuery<any[]>({
    queryKey: ["/api/availability-exceptions", selectedDoctorId],
    queryFn: async () => {
      const url = selectedDoctorId 
        ? `/api/availability-exceptions?doctorId=${selectedDoctorId}`
        : "/api/availability-exceptions";
      const res = await fetch(url, { credentials: "include" });
      return res.json();
    }
  });

  const toggleAvailability = useMutation({
    mutationFn: async ({ doctorId, dates, isAvailable }: any) => {
      if (isAvailable) {
        // If we are making it available, we delete existing exceptions
        await apiRequest("POST", "/api/availability-exceptions/bulk-delete", { doctorId, dates });
      } else {
        // Blocking it
        await apiRequest("POST", "/api/availability-exceptions", { doctorId, dates, isAvailable: false });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/availability-exceptions"] });
      toast({ title: "Agenda atualizada", description: "Disponibilidade alterada com sucesso." });
      setIsAvailabilityDialogOpen(false);
    }
  });

  const { toast } = useToast();
  const createAppointment = useCreateAppointment();
  const [selectedAppointmentForCheckin, setSelectedAppointmentForCheckin] = useState<AppointmentWithDetails | null>(null);
  const [isCheckinDialogOpen, setIsCheckinDialogOpen] = useState(false);

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status, paymentMethod, paymentStatus }: any) => {
      const res = await apiRequest("PATCH", `/api/appointments/${id}/status`, { 
        status, 
        paymentMethod, 
        paymentStatus 
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [api.appointments.list.path] });
      toast({ title: "Sucesso", description: "Status do agendamento atualizado" });
      setIsCheckinDialogOpen(false);
      setSelectedAppointmentForCheckin(null);
    }
  });

  const checkinForm = useForm({
    defaultValues: {
      paymentMethod: "dinheiro",
      paymentStatus: "pendente",
      confirmData: false
    }
  });

  const onCheckinSubmit = (data: any) => {
    if (!selectedAppointmentForCheckin) return;
    updateStatusMutation.mutate({
      id: selectedAppointmentForCheckin.id,
      status: "presente",
      paymentMethod: data.paymentMethod,
      paymentStatus: data.paymentStatus
    });
  };

  const updateAppointment = useMutation({
    mutationFn: async (data: any) => {
      const res = await apiRequest("PUT", `/api/appointments/${editingAppointment?.id}`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [api.appointments.list.path] });
      toast({ title: "Sucesso", description: "Agendamento atualizado" });
      setIsAptDialogOpen(false);
      setEditingAppointment(null);
    },
    onError: (error: Error) => {
      toast({ 
        title: "Conflito de Horário", 
        description: error.message, 
        variant: "destructive" 
      });
    }
  });

  const deleteAppointment = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/appointments/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [api.appointments.list.path] });
      toast({ title: "Sucesso", description: "Agendamento excluído" });
      setIsAptDialogOpen(false);
      setEditingAppointment(null);
    }
  });

  const aptForm = useForm({
    resolver: zodResolver(insertAppointmentSchema),
    defaultValues: {
      patientId: 0,
      doctorId: 0,
      date: format(new Date(), 'yyyy-MM-dd'),
      startTime: "09:00",
      duration: 30,
      status: "agendado",
      notes: "",
      clinicId: 1,
      price: 150,
      type: "consulta",
      examType: "",
      procedure: "",
      insurance: "",
      isPrivate: false
    }
  });

  useEffect(() => {
    if (editingAppointment) {
      aptForm.reset({
        patientId: editingAppointment.patientId,
        doctorId: editingAppointment.doctorId,
        date: editingAppointment.date,
        startTime: editingAppointment.startTime,
        duration: editingAppointment.duration,
        status: editingAppointment.status,
        notes: editingAppointment.notes || "",
        clinicId: editingAppointment.clinicId,
        price: editingAppointment.price / 100,
        type: (editingAppointment as any).type || "consulta",
        examType: (editingAppointment as any).examType || "",
        procedure: (editingAppointment as any).procedure || "",
        insurance: (editingAppointment as any).insurance || "",
        isPrivate: (editingAppointment as any).isPrivate || false
      });
    } else {
      aptForm.reset({
        patientId: 0,
        doctorId: 0,
        date: format(new Date(), 'yyyy-MM-dd'),
        startTime: "09:00",
        duration: 30,
        status: "agendado",
        notes: "",
        clinicId: 1,
        price: 150,
        type: "consulta",
        examType: "",
        procedure: "",
        insurance: "",
        isPrivate: false
      });
    }
  }, [editingAppointment]);

  const onAptSubmit = async (data: any) => {
    try {
      const formattedData = {
        ...data,
        price: Math.round(data.price * 100)
      };
      if (editingAppointment) {
        await updateAppointment.mutateAsync(formattedData);
      } else {
        await createAppointment.mutateAsync(formattedData);
        setIsAptDialogOpen(false);
      }
      aptForm.reset();
    } catch (error: any) {
      // Error handled by mutation onError
    }
  };

  const [statusFilter, setStatusFilter] = useState<string>("all");

  const calendarEvents = allAppointments?.map(apt => {
    const startStr = `${apt.date}T${apt.startTime}`;
    const startDate = parseISO(startStr);
    const endDate = addMinutes(startDate, apt.duration);

    let bgColor = '#3b82f6'; // default blue
    let textColor = '#ffffff';
    let borderColor = 'transparent';

    if (apt.status === 'finalizado') { bgColor = '#dcfce7'; textColor = '#166534'; borderColor = '#bbf7d0'; } // green-100/green-800
    if (apt.status === 'presente') { bgColor = '#fef3c7'; textColor = '#92400e'; borderColor = '#fde68a'; } // amber-100/amber-800
    if (apt.status === 'cancelado') { bgColor = '#fee2e2'; textColor = '#991b1b'; borderColor = '#fecaca'; } // red-100/red-800
    if (apt.status === 'em_atendimento') { bgColor = '#f3e8ff'; textColor = '#6b21a8'; borderColor = '#e9d5ff'; } // purple-100/purple-800
    if (apt.status === 'confirmado') { bgColor = '#dbeafe'; textColor = '#1e40af'; borderColor = '#bfdbfe'; } // blue-100/blue-800

    return {
      id: apt.id.toString(),
      title: `${apt.patient.name} (Dr. ${apt.doctor.name})`,
      start: startStr,
      end: format(endDate, "yyyy-MM-dd'T'HH:mm:ss"),
      backgroundColor: bgColor,
      textColor: textColor,
      borderColor: borderColor,
      extendedProps: { appointment: apt }
    };
  }) || [];

  const filteredEvents = calendarEvents.filter(e => {
    const apt = e.extendedProps.appointment;
    const matchesDoctor = !selectedDoctorId || apt.doctorId === selectedDoctorId;
    const matchesStatus = statusFilter === "all" || apt.status === statusFilter;
    return matchesDoctor && matchesStatus;
  });

  const { user } = useAuth();
  const isReadOnly = user?.role === 'nurse';

  return (
    <LayoutShell>
      <div className="space-y-8">
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-display font-bold text-slate-900">Agenda Completa</h1>
            <p className="text-muted-foreground mt-2">
              {isReadOnly 
                ? "Visualize os agendamentos da clínica" 
                : "Visualize e gerencie todos os agendamentos da clínica"}
            </p>
          </div>
          <div className="flex items-center gap-3">
            <Select 
              onValueChange={(v) => setSelectedDoctorId(v === "all" ? undefined : Number(v))} 
              value={selectedDoctorId?.toString() || "all"}
            >
              <SelectTrigger className="w-[180px] bg-white">
                <SelectValue placeholder="Médico" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos Médicos</SelectItem>
                {doctors?.map(d => (
                  <SelectItem key={d.id} value={d.id.toString()}>{d.name}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select 
              onValueChange={setStatusFilter} 
              value={statusFilter}
            >
              <SelectTrigger className="w-[180px] bg-white">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos Status</SelectItem>
                <SelectItem value="agendado">Agendado</SelectItem>
                <SelectItem value="confirmado">Confirmado</SelectItem>
                <SelectItem value="presente">Aguardando</SelectItem>
                <SelectItem value="em_atendimento">Em Atendimento</SelectItem>
                <SelectItem value="finalizado">Finalizado</SelectItem>
              </SelectContent>
            </Select>
            {!isReadOnly && (
              <>
                <Button onClick={() => { setEditingAppointment(null); setIsAptDialogOpen(true); }} className="gap-2">
                  <Plus className="w-4 h-4" /> Agendar Consulta
                </Button>
                {selectedDoctorId && (
                  <Button 
                    variant="outline" 
                    onClick={() => setIsAvailabilityDialogOpen(true)}
                  >
                    Bloquear/Desbloquear Agenda
                  </Button>
                )}
              </>
            )}
          </div>
        </div>

        <Dialog open={isAvailabilityDialogOpen} onOpenChange={setIsAvailabilityDialogOpen}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>Gerenciar Disponibilidade</DialogTitle>
            </DialogHeader>
            <div className="space-y-4 py-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">Período</label>
                <DateRangePicker 
                  date={availabilityDateRange}
                  setDate={setAvailabilityDateRange}
                />
              </div>
              <div className="flex gap-4">
                <Button 
                  className="flex-1"
                  variant="destructive"
                  onClick={() => {
                    if (!availabilityDateRange?.from || !selectedDoctorId) return;
                    const dates = eachDayOfInterval({
                      start: availabilityDateRange.from,
                      end: availabilityDateRange.to || availabilityDateRange.from
                    }).map(d => format(d, 'yyyy-MM-dd'));

                    toggleAvailability.mutate({
                      doctorId: selectedDoctorId,
                      dates,
                      isAvailable: false
                    });
                  }}
                  disabled={toggleAvailability.isPending}
                >
                  Fechar Agenda
                </Button>
                <Button 
                  className="flex-1"
                  variant="outline"
                  onClick={() => {
                    if (!availabilityDateRange?.from || !selectedDoctorId) return;
                    const dates = eachDayOfInterval({
                      start: availabilityDateRange.from,
                      end: availabilityDateRange.to || availabilityDateRange.from
                    }).map(d => format(d, 'yyyy-MM-dd'));

                    toggleAvailability.mutate({
                      doctorId: selectedDoctorId,
                      dates,
                      isAvailable: true
                    });
                  }}
                  disabled={toggleAvailability.isPending}
                >
                  Abrir Agenda
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>

        <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard 
            title="Agendamentos no Período" 
            value={allAppointments?.length || 0} 
            icon={Calendar} 
            color="primary"
          />
          <StatCard 
            title="Aguardando" 
            value={allAppointments?.filter(a => a.status === 'presente').length || 0} 
            icon={Clock} 
            color="orange"
          />
          <StatCard 
            title="Médicos" 
            value={doctors?.length || 0} 
            icon={Users} 
            color="accent"
          />
          <StatCard 
            title="Pendentes" 
            value={allAppointments?.filter(a => a.status === 'agendado').length || 0} 
            icon={AlertCircle} 
            color="purple"
          />
        </div>

        <Card className="border-none shadow-md overflow-hidden bg-white">
          <CardContent className="p-6">
            <div className="calendar-container">
              <FullCalendar
                plugins={[timeGridPlugin, dayGridPlugin, interactionPlugin]}
                initialView="timeGridWeek"
                headerToolbar={{
                  left: 'prev,next today',
                  center: 'title',
                  right: 'dayGridMonth,timeGridWeek,timeGridDay'
                }}
                events={filteredEvents}
                locale="pt-br"
                buttonText={{
                  today: 'Hoje',
                  month: 'Mês',
                  week: 'Semana',
                  day: 'Dia'
                }}
                slotMinTime="08:00:00"
                slotMaxTime="20:00:00"
                allDaySlot={false}
                height="auto"
                nowIndicator={true}
                dayCellClassNames={(arg) => {
                  const dateStr = format(arg.date, 'yyyy-MM-dd');
                  const isBlocked = availabilityExceptions?.some(ex => 
                    ex.date === dateStr && 
                    (!selectedDoctorId || ex.doctorId === selectedDoctorId) &&
                    !ex.isAvailable
                  );
                  return isBlocked ? 'bg-slate-100 opacity-60 cursor-not-allowed blocked-day' : '';
                }}
                datesSet={(arg) => {
                  setViewRange({
                    start: format(arg.start, 'yyyy-MM-dd'),
                    end: format(arg.end, 'yyyy-MM-dd')
                  });
                }}
                eventClick={(info) => {
                  if (isReadOnly) return;
                  const apt = info.event.extendedProps.appointment;
                  if (apt.status === 'confirmado') {
                    setSelectedAppointmentForCheckin(apt);
                    setIsCheckinDialogOpen(true);
                  } else {
                    setEditingAppointment(apt);
                    setIsAptDialogOpen(true);
                  }
                }}
                dateClick={(info) => {
                  if (isReadOnly) return;
                  const dateStr = info.dateStr.split('T')[0];
                  const isBlocked = availabilityExceptions?.some(ex => 
                    ex.date === dateStr && 
                    (!selectedDoctorId || ex.doctorId === selectedDoctorId) &&
                    !ex.isAvailable
                  );

                  if (isBlocked) {
                    toast({ 
                      title: "Agenda Fechada", 
                      description: "Esta data está bloqueada para agendamentos.",
                      variant: "destructive"
                    });
                    return;
                  }

                  setEditingAppointment(null);
                  aptForm.reset({
                    patientId: 0,
                    doctorId: selectedDoctorId || 0,
                    date: info.dateStr.split('T')[0],
                    startTime: info.dateStr.split('T')[1]?.substring(0, 5) || "09:00",
                    duration: 30,
                    status: "agendado",
                    notes: "",
                    clinicId: 1
                  });
                  setIsAptDialogOpen(true);
                }}
              />
            </div>
          </CardContent>
        </Card>
      </div>

      <Dialog open={isCheckinDialogOpen} onOpenChange={setIsCheckinDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Check-in do Paciente</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="bg-slate-50 p-4 rounded-md space-y-2">
              <p className="text-sm font-medium">Paciente: <span className="font-bold">{selectedAppointmentForCheckin?.patient.name}</span></p>
              <p className="text-sm">CPF: {selectedAppointmentForCheckin?.patient.cpf || 'Não informado'}</p>
              <p className="text-sm">Telefone: {selectedAppointmentForCheckin?.patient.phone || 'Não informado'}</p>
            </div>

            <Form {...checkinForm}>
              <form onSubmit={checkinForm.handleSubmit(onCheckinSubmit)} className="space-y-4">
                <FormField
                  control={checkinForm.control}
                  name="paymentMethod"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Forma de Pagamento</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Selecione o pagamento" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="dinheiro">Dinheiro</SelectItem>
                          <SelectItem value="cartao_credito">Cartão de Crédito</SelectItem>
                          <SelectItem value="cartao_debito">Cartão de Débito</SelectItem>
                          <SelectItem value="pix">PIX</SelectItem>
                          <SelectItem value="convenio">Convênio</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormItem>
                  )}
                />

                <FormField
                  control={checkinForm.control}
                  name="paymentStatus"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Status do Pagamento</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Status do pagamento" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="pendente">Pendente</SelectItem>
                          <SelectItem value="pago">Pago</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormItem>
                  )}
                />

                <Button type="submit" className="w-full" disabled={updateStatusMutation.isPending}>
                  Confirmar Dados e Check-in
                </Button>
              </form>
            </Form>
          </div>
        </DialogContent>
      </Dialog>

      <Dialog open={isAptDialogOpen} onOpenChange={setIsAptDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="text-2xl font-bold text-slate-800">
              {editingAppointment ? "Editar Agendamento" : "Novo Agendamento"}
            </DialogTitle>
          </DialogHeader>
          <Form {...aptForm}>
            <form onSubmit={aptForm.handleSubmit(onAptSubmit)} className="space-y-6">
              <Tabs defaultValue="basic" className="w-full">
                <TabsList className="grid w-full grid-cols-3 mb-4">
                  <TabsTrigger value="basic">Informações Básicas</TabsTrigger>
                  <TabsTrigger value="details">Detalhes</TabsTrigger>
                  <TabsTrigger value="payment">Financeiro</TabsTrigger>
                </TabsList>

                <TabsContent value="basic" className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={aptForm.control}
                      name="patientId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Paciente</FormLabel>
                          <Select onValueChange={(v) => field.onChange(Number(v))} value={field.value?.toString()}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Selecione um paciente" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {patients?.map(p => (
                                <SelectItem key={p.id} value={p.id.toString()}>{p.name}</SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={aptForm.control}
                      name="doctorId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Médico</FormLabel>
                          <Select onValueChange={(v) => field.onChange(Number(v))} value={field.value?.toString()}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Selecione um médico" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {doctors?.map(d => (
                                <SelectItem key={d.id} value={d.id.toString()}>{d.name}</SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={aptForm.control}
                      name="date"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Data</FormLabel>
                          <FormControl>
                            <Input type="date" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div className="grid grid-cols-2 gap-2">
                      <FormField
                        control={aptForm.control}
                        name="startTime"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Horário</FormLabel>
                            <FormControl>
                              <Input type="time" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={aptForm.control}
                        name="duration"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Duração (min)</FormLabel>
                            <FormControl>
                              <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>
                </TabsContent>

                <TabsContent value="details" className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={aptForm.control}
                      name="type"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Tipo de Agendamento</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Selecione o tipo" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="consulta">Consulta</SelectItem>
                              <SelectItem value="retorno">Retorno</SelectItem>
                              <SelectItem value="exame">Exame</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    {aptForm.watch("type") === "exame" && (
                      <FormField
                        control={aptForm.control}
                        name="examType"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Nome do Exame</FormLabel>
                            <FormControl>
                              <Input placeholder="Ex: Hemograma" {...field} value={field.value || ""} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}
                  </div>

                  <FormField
                    control={aptForm.control}
                    name="procedure"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Procedimento</FormLabel>
                        <FormControl>
                          <Input placeholder="Ex: Limpeza, Canal, etc." {...field} value={field.value || ""} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={aptForm.control}
                    name="notes"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Observações</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Notas adicionais sobre o agendamento..." 
                            className="resize-none"
                            {...field} 
                            value={field.value || ""}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </TabsContent>

                <TabsContent value="payment" className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={aptForm.control}
                      name="isPrivate"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                          <FormControl>
                            <Checkbox
                              checked={field.value}
                              onCheckedChange={(checked) => {
                                field.onChange(checked);
                                if (checked) {
                                  aptForm.setValue("insurance", "");
                                }
                              }}
                            />
                          </FormControl>
                          <div className="space-y-1 leading-none">
                            <FormLabel>Particular</FormLabel>
                            <FormDescription>
                              Sem uso de convênio
                            </FormDescription>
                          </div>
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={aptForm.control}
                      name="insurance"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Convênio</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="Nome do convênio" 
                              {...field} 
                              value={field.value || ""} 
                              disabled={aptForm.watch("isPrivate")}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={aptForm.control}
                      name="price"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Valor (R$)</FormLabel>
                          <FormControl>
                            <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={aptForm.control}
                      name="status"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Status</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Selecione o status" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="agendado">Agendado</SelectItem>
                              <SelectItem value="confirmado">Confirmado</SelectItem>
                              <SelectItem value="cancelado">Cancelado</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                </TabsContent>
              </Tabs>

              <DialogFooter className="flex gap-2 pt-4 border-t">
                {editingAppointment && (
                  <Button 
                    type="button" 
                    variant="destructive" 
                    className="flex-1"
                    onClick={() => {
                      if (confirm("Deseja realmente excluir este agendamento?")) {
                        deleteAppointment.mutate(editingAppointment.id);
                      }
                    }}
                    disabled={deleteAppointment.isPending}
                  >
                    Excluir
                  </Button>
                )}
                <Button 
                  type="submit" 
                  className="flex-[2]" 
                  disabled={createAppointment.isPending || updateAppointment.isPending}
                >
                  {(createAppointment.isPending || updateAppointment.isPending) && (
                    <Loader2 className="w-4 h-4 animate-spin mr-2" />
                  )}
                  {editingAppointment ? "Salvar Alterações" : "Criar Agendamento"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </LayoutShell>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

shared/schema.ts.content:
import { sqliteTable, text, integer } from "drizzle-orm/sqlite-core";
import { relations, sql } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// === TABLE DEFINITIONS ===

export const clinics = sqliteTable("clinics", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  name: text("name").notNull(),
  address: text("address").notNull(),
  phone: text("phone").notNull(),
  subscriptionStatus: text("subscription_status").default("active").notNull(), // active, inactive
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const inventory = sqliteTable("inventory", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),
  name: text("name").notNull(),
  category: text("category").notNull(), // 'material', 'medicamento', 'equipamento', 'outro'
  unit: text("unit").notNull(), // 'unidade', 'ml', 'mg', 'caixa', 'frasco', 'pacote'
  quantity: integer("quantity").default(0).notNull(),
  minQuantity: integer("min_quantity").default(5).notNull(),
  pricePerUnit: integer("price_per_unit").default(0).notNull(),
  supplier: text("supplier"),
  location: text("location"),
  batchNumber: text("batch_number"),
  expiryDate: text("expiry_date"),
  description: text("description"),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const inventoryTransactions = sqliteTable("inventory_transactions", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  inventoryId: integer("inventory_id").references(() => inventory.id).notNull(),
  type: text("type").notNull(), // 'entrada', 'saida', 'baixa_automatica'
  quantity: integer("quantity").notNull(),
  appointmentId: integer("appointment_id").references(() => appointments.id),
  notes: text("notes"),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const tissBills = sqliteTable("tiss_bills", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),
  appointmentId: integer("appointment_id").references(() => appointments.id).notNull(),
  patientId: integer("patient_id").references(() => patients.id).notNull(),
  insuranceId: text("insurance_id").notNull(),
  status: text("status").default("pendente").notNull(), // pendente, gerada, enviada, paga
  xmlData: text("xml_data"),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const digitalSignatures = sqliteTable("digital_signatures", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  medicalRecordId: integer("medical_record_id").references(() => medicalRecords.id).notNull(),
  doctorId: integer("doctor_id").references(() => users.id).notNull(),
  signatureHash: text("signature_hash").notNull(),
  certificateInfo: text("certificate_info"),
  signedAt: text("signed_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const users = sqliteTable("users", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  name: text("name").notNull(),
  role: text("role").notNull(), // 'admin', 'operator', 'doctor', 'nurse'
  specialty: text("specialty"), // For doctors
  clinicId: integer("clinic_id").references(() => clinics.id),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const patients = sqliteTable("patients", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  name: text("name").notNull(),
  cpf: text("cpf"),
  birthDate: text("birth_date").notNull(),
  phone: text("phone"),
  email: text("email"),
  gender: text("gender"),
  address: text("address"),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const appointments = sqliteTable("appointments", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  patientId: integer("patient_id").references(() => patients.id).notNull(),
  doctorId: integer("doctor_id").references(() => users.id).notNull(),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),
  date: text("date").notNull(),
  startTime: text("start_time").notNull(), // HH:mm
  duration: integer("duration").default(30).notNull(), // minutes
  price: integer("price").default(15000).notNull(), // Valor em centavos (R$ 150,00)
  type: text("type").default("consulta").notNull(), // consulta, retorno, exame
  examType: text("exam_type"), // Ex: Sangue, Raio-X, etc.
  status: text("status").default("agendado").notNull(), // agendado, confirmado, presente, em_atendimento, finalizado, cancelado, remarcado, ausente
  paymentMethod: text("payment_method"), // dinheiro, cartao_credito, cartao_debito, pix, convenio
  paymentStatus: text("payment_status").default("pendente").notNull(), // pendente, pago
  procedure: text("procedure"),
  insurance: text("insurance"),
  isPrivate: integer("is_private", { mode: "boolean" }).default(false).notNull(),
  notes: text("notes"),
  triageDone: integer("triage_done", { mode: "boolean" }).default(false).notNull(),
  triageData: text("triage_data", { mode: "json" }).$type<{
    weight?: string;
    height?: string;
    bloodPressure?: string;
    temperature?: string;
    heartRate?: string;
    respiratoryRate?: string;
    oxygenSaturation?: string;
    notes?: string;
  }>(),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const medicalRecordLogs = sqliteTable("medical_record_logs", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  medicalRecordId: integer("medical_record_id").references(() => medicalRecords.id).notNull(),
  userId: integer("user_id").references(() => users.id).notNull(),
  action: text("action").notNull(), // 'create', 'update', 'delete', 'sign'
  changes: text("changes", { mode: "json" }),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const medicalRecords = sqliteTable("medical_records", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  appointmentId: integer("appointment_id").references(() => appointments.id),
  patientId: integer("patient_id").references(() => patients.id).notNull(),
  doctorId: integer("doctor_id").references(() => users.id).notNull(),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),

  // Anamnesis
  chiefComplaint: text("chief_complaint"),
  history: text("history"),
  medications: text("medications"),
  allergies: text("allergies"),
  vitals: text("vitals", { mode: "json" }).$type<{
    bloodPressure?: string;
    heartRate?: string;
    temperature?: string;
    weight?: string;
    height?: string;
  }>(),

  // Clinical Evolution
  diagnosis: text("diagnosis"),
  prescription: text("prescription"),
  notes: text("notes"), // Evolution notes

  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

export const availabilityExceptions = sqliteTable("availability_exceptions", {
  id: integer("id").primaryKey({ autoIncrement: true }),
  doctorId: integer("doctor_id").references(() => users.id).notNull(),
  clinicId: integer("clinic_id").references(() => clinics.id).notNull(),
  date: text("date").notNull(),
  isAvailable: integer("is_available", { mode: "boolean" }).default(false).notNull(),
  reason: text("reason"),
  createdAt: text("created_at").default(sql`(CURRENT_TIMESTAMP)`),
});

// === RELATIONS ===

export const clinicsRelations = relations(clinics, ({ many }) => ({
  users: many(users),
  patients: many(patients),
  appointments: many(appointments),
  inventory: many(inventory),
  tissBills: many(tissBills),
}));

export const inventoryRelations = relations(inventory, ({ one, many }) => ({
  clinic: one(clinics, {
    fields: [inventory.clinicId],
    references: [clinics.id],
  }),
  transactions: many(inventoryTransactions),
}));

export const tissBillsRelations = relations(tissBills, ({ one }) => ({
  appointment: one(appointments, {
    fields: [tissBills.appointmentId],
    references: [appointments.id],
  }),
  patient: one(patients, {
    fields: [tissBills.patientId],
    references: [patients.id],
  }),
}));

export const medicalRecordsRelations = relations(medicalRecords, ({ one, many }) => ({
  appointment: one(appointments, {
    fields: [medicalRecords.appointmentId],
    references: [appointments.id],
  }),
  patient: one(patients, {
    fields: [medicalRecords.patientId],
    references: [patients.id],
  }),
  doctor: one(users, {
    fields: [medicalRecords.doctorId],
    references: [users.id],
    relationName: "doctorRecords",
  }),
  signatures: many(digitalSignatures),
}));

export const usersRelations = relations(users, ({ one, many }) => ({
  clinic: one(clinics, {
    fields: [users.clinicId],
    references: [clinics.id],
  }),
  doctorAppointments: many(appointments, { relationName: "doctorAppointments" }),
  recordsAuthored: many(medicalRecords, { relationName: "doctorRecords" }),
}));

// === SCHEMAS ===

export const insertClinicSchema = createInsertSchema(clinics).omit({ id: true, createdAt: true });
export const insertUserSchema = createInsertSchema(users).omit({ id: true, createdAt: true });
export const insertPatientSchema = createInsertSchema(patients).omit({ id: true, createdAt: true });
export const insertAppointmentSchema = createInsertSchema(appointments).omit({ id: true, createdAt: true });
export const insertMedicalRecordSchema = createInsertSchema(medicalRecords).omit({ id: true, createdAt: true });
export const insertInventorySchema = createInsertSchema(inventory).omit({ id: true, createdAt: true });
export const insertInventoryTransactionSchema = createInsertSchema(inventoryTransactions).omit({ id: true, createdAt: true });
export const insertTissBillSchema = createInsertSchema(tissBills).omit({ id: true, createdAt: true });
export const insertDigitalSignatureSchema = createInsertSchema(digitalSignatures).omit({ id: true, signedAt: true });
export const insertAvailabilityExceptionSchema = createInsertSchema(availabilityExceptions).omit({ id: true, createdAt: true });
export const insertMedicalRecordLogSchema = createInsertSchema(medicalRecordLogs).omit({ id: true, createdAt: true });

// === EXPLICIT TYPES ===

export type Clinic = typeof clinics.$inferSelect;
export type User = typeof users.$inferSelect;
export type Patient = typeof patients.$inferSelect;
export type Appointment = typeof appointments.$inferSelect;
export type MedicalRecord = typeof medicalRecords.$inferSelect;

export type MedicalRecordLog = typeof medicalRecordLogs.$inferSelect;
export type InsertMedicalRecordLog = z.infer<typeof insertMedicalRecordLogSchema>;
export type Inventory = typeof inventory.$inferSelect;
export type InventoryTransaction = typeof inventoryTransactions.$inferSelect;
export type TissBill = typeof tissBills.$inferSelect;
export type DigitalSignature = typeof digitalSignatures.$inferSelect;
export type AvailabilityException = typeof availabilityExceptions.$inferSelect;

export type InsertInventory = z.infer<typeof insertInventorySchema>;
export type InsertInventoryTransaction = z.infer<typeof insertInventoryTransactionSchema>;
export type InsertTissBill = z.infer<typeof insertTissBillSchema>;
export type InsertDigitalSignature = z.infer<typeof insertDigitalSignatureSchema>;
export type InsertAvailabilityException = z.infer<typeof insertAvailabilityExceptionSchema>;

export type InsertClinic = z.infer<typeof insertClinicSchema>;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type InsertPatient = z.infer<typeof insertPatientSchema>;
export type InsertAppointment = z.infer<typeof insertAppointmentSchema>;
export type InsertMedicalRecord = z.infer<typeof insertMedicalRecordSchema>;

export type CreateAppointmentRequest = InsertAppointment;
export type UpdateAppointmentStatusRequest = {
  status: string;
  paymentMethod?: string;
  paymentStatus?: string;
  price?: number;
  triageData?: any;
};

// Augmented types for frontend display
export type AppointmentWithDetails = Appointment & {
  patient: Patient;
  doctor: User;
};

export type MedicalRecordWithDetails = MedicalRecord & {
  patient: Patient;
  doctor: User;
};

client/src/components/ui/carousel.tsx.content:
import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

client/src/components/ui/chart.tsx.content:
"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

